/** Public configuration options for the arcade assistant */
export type ArcadeAssistantConfig = {
    /** The URL of the portal the arcade editor is loaded for. Typically accessed via `portal.url` */
    portalUrl: string;
    /** A unique key for your application, used to identify the app in the feedback system.
     * Typically you will not need to provide this, granted that you have set `esriConfig.applicationName` in your app.
     */
    appVersion?: string;
};
export type ArcadeAssistantRemoteState = {
    shouldRender: true;
    serviceUrl: string;
    feedbackServiceUrl: string;
    helpBase: string;
    assistantsEnabled: boolean;
} | {
    shouldRender: false;
};
export interface PortalSelfResponse {
    helperServices?: {
        aiAssistantServices?: {
            url?: string;
        };
        [key: string]: unknown;
    };
    helpBase?: string;
    [key: string]: unknown;
}
export interface PortalSettingsResponse {
    aiAssistantsEnabled?: boolean;
    [key: string]: unknown;
}
export interface SignInSettingsResponse {
    blockBetaApps?: boolean;
}
