declare namespace SunCalc {
  interface Position {
    azimuth: number;
    altitude: number;
  }

  enum PolarException {
    NORMAL,
    MIDNIGHT_SUN,
    POLAR_NIGHT,
  }

  interface Times {
    solarNoon: Date;
    nadir: Date;
    polarException: PolarException;
    sunrise: Date;
    sunset: Date;
  }

  function getPosition(date: Date, lat: number, lng: number, ret: Position): Position;
  function getTimes(date: Date, lat: number, lng: number): Times;
}

export default SunCalc;
